function getMemberTemplate(){$.ajax({url:"tmpl/member.tmpl",dataType:"html",success:function(a){$("head").append(a),updateMemberTable()}})}function buildMemberRows(a){return _.template($("#member-tmpl").html(),{members:a})}function updateMemberTable(){$.ajax({url:"rest/members/json",cache:!1,success:function(a){$("#members").empty().append(buildMemberRows(a))},error:function(a){}})}function registerMember(a){$("span.invalid").remove(),$("span.success").remove(),$.post("rest/members",a,function(a){$("#reg")[0].reset(),$("#formMsgs").append($('<span class="success">Member Registered</span>')),updateMemberTable()}).error(function(a){if(a.status==409||a.status==400){var b=$.parseJSON(a.responseText);$.each(b,function(a,b){$('<span class="invalid">'+b+"</span>").insertAfter($("#"+a))})}else $("#formMsgs").append($('<span class="invalid">Unknown server error</span>'))})}function touchScrollX(a){if(Modernizr.touch){var b=document.querySelector(a),c=0;b.addEventListener("touchstart",function(a){c=this.scrollLeft+a.touches[0].pageX,a.preventDefault()},!1),b.addEventListener("touchmove",function(a){this.scrollLeft=c-a.touches[0].pageX,a.preventDefault()},!1)}}